const ids_chave = $input.first().json['ID Post'] || [];
const msg_chave = $input.first().json['Mensagem de Ativação'] || [];
const acao_chave = $input.first().json['Ação'] || [];
const txt_msg_chave = $input.first().json['Texto da Mensagem'] || [];
const link_chave = $input.first().json.Link || [];
const txt_btn_chave = $input.first().json['Texto do Botão'] || [];
const url_video_chave = $input.first().json['URL do Vídeo'] || [];
const url_img_chave = $input.first().json['URL da Imagem'] || [];

if (!Array.isArray(ids_chave) || !Array.isArray(msg_chave)) {
    throw new Error("Os dados de entrada não são arrays válidos.");
}

const id_msg = ids_chave.map((id, index) => ({
    id: id,
    msg: msg_chave[index] || "none",
    acao: acao_chave[index] || "none",
    txt_msg: txt_msg_chave[index] || "none",
    link: link_chave[index] || "none",
    botao: txt_btn_chave[index] || "none",
    video: url_video_chave[index] || "none",
    imagem: url_img_chave[index] || "none"
}));

return { json: { id_msg } };

